export const CarouselItem = ({ manga, width }) => {
  return (
    <div className="carousel-item" style={{ width: width }}>
      <div></div>
      <img className="carousel-img" src={manga.img_url} />
      <div className="carousel-item-text"><a href={manga.url}>{manga.title}</a></div>
      <div className="carousel-item-text"><p>Description:</p>{manga.manga_description}</div>
    </div>
  );
};
